import { Router } from '@angular/router';
@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css']
})
export class InicioComponent implements OnInit {
  saludo:string=""
  editarVal=false;
  mostrarMensaje: boolean = true;
  usuarios:any=[]
  anuncio:any={}
  verAnuncio:any=[]
  selectedFile:any
  imageUrl: string | null = null;
 imagen="";
 imagenF=""
