import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-lugares',
  templateUrl: './lugares.component.html',
  styleUrls: ['./lugares.component.css']
})
export class LugaresComponent {
         formulario:any;
         imageUrl: string | ArrayBuffer | null = null;
         selectedFile: File | null = null;







  constructor() { }

  ngOnInit(): void {
    this.formulario = new FormGroup({
      nombre: new FormControl('',Validators.required),
      descripcion: new FormControl('',Validators.required),
      imagen: new FormControl('',Validators.required)
    });

  }

  onFileSelected(event: any) {
    const file: File = event.target.files[0];
    if (file) {
      this.selectedFile = file;

      // Mostrar una vista previa de la imagen
      const reader = new FileReader();
      reader.onload = (e) => {
        this.imageUrl = e.target?.result || null; // Usa null como valor predeterminado si es 'undefined'
      };
      reader.readAsDataURL(file);
    } else {
      this.imageUrl = null; // Establece imageUrl en null si no se selecciona ningún archivo
    }
  }


 }
  guardar(){
    console.log(this.formulario.value);
  }

   uploadImage() {
    const fileInput = document.getElementById('fileInput');
    const previewImage = document.getElementById('previewImage');

    const file = fileInput.files[0];

    if (file) {
        const reader = new FileReader();
        reader.onload = function (e) {
            previewImage.src = e.target.result;
        };
        reader.readAsDataURL(file);
    }
}




}
